mapper.update:
UPDATE annual_leave
SET total_days = #{totalDays},
    remain_days = #{remainDays},
    updated_at = #{updatedAt},
    updated_by = #{updatedBy}
WHERE mem_id = #{memId}
  AND year = #{year}

