mapper.select:
SELECT schedule_id, schedule_title, schedule_content, start_at, end_at FROM company_schedule
AND schedule_title LIKE CONCAT('%', #{keyword}, '%')
AND DATE_FORMAT(start_at, '%Y-%m-%d') = #{keyword}
ORDER BY start_at DESC LIMIT #{startNum}, #{itemsPerPage}

SELECT COUNT(*) FROM company_schedule
AND schedule_title LIKE CONCAT('%', #{keyword}, '%')
AND DATE_FORMAT(start_at, '%Y-%m-%d') = #{keyword}
