mapper.select:
SELECT mem_pass
FROM member
WHERE mem_id = #{memId}

SELECT mem_status
FROM member
WHERE mem_id = #{memId}

mapper.update:
UPDATE member
SET mem_phone         = #{memPhone},
    mem_private_email = #{privateEmail},
    mem_address       = #{memAddress}
WHERE mem_id = #{memId}

mapper.select:
SELECT COUNT(*) FROM member m LEFT JOIN dept d ON m.dept_id = d.dept_id LEFT JOIN rank r ON m.rank_id = r.rank_id WHERE 1=1
AND m.dept_id = #{dept}
AND m.rank_id = #{rank}
AND m.mem_name LIKE CONCAT('%', #{name}, '%')

SELECT m.mem_id, m.mem_name, m.dept_id, m.rank_id, d.dept_name, r.rank_name
FROM member m
LEFT JOIN dept d ON m.dept_id = d.dept_id
LEFT JOIN rank r ON m.rank_id = r.rank_id
WHERE 1=1

    AND d.dept_id = #{dept}


    AND r.rank_id = #{rank}


    AND m.mem_name LIKE CONCAT('%', #{name}, '%')

ORDER BY m.mem_id DESC
LIMIT #{startNum}, #{itemsPerPage}

mapper.update:
UPDATE member
SET dept_id = #{deptId},
    rank_id = #{rankId}
WHERE mem_id = #{memId}

UPDATE member
SET mem_pass = #{encodedPassword}
WHERE mem_id = #{memId}

mapper.select:
SELECT mem_private_email
from member
where mem_id = #{memId}

SELECT COUNT(*) > 0
FROM member
WHERE mem_id = #{memId}
  AND mem_name = #{memName}
  AND mem_private_email = #{memPrivateEmail}
