mapper.select:
SELECT mem_id
FROM member
WHERE mem_id = #{id}
  AND mem_pass = #{password}
  AND mem_status = '재직'

mapper.update:
UPDATE member
SET
    mem_phone = #{phone},
    mem_private_email = #{privateEmail},
    mem_address = #{address}
WHERE mem_id = #{memId}

mapper.delete:
DELETE FROM attachment
WHERE business_id = #{memId}
  AND attach_type = 'P'

mapper.select:
SELECT COUNT(*) FROM member m LEFT JOIN dept d ON m.dept_id = d.dept_id LEFT JOIN rank r ON m.rank_id = r.rank_id WHERE 1=1
AND d.dept_name = #{dept}
AND r.rank_name = #{rank}
AND m.mem_name LIKE CONCAT('%', #{name}, '%')

SELECT m.mem_id, m.mem_name, m.dept_id, m.rank_id, d.dept_name, r.rank_name FROM member m LEFT JOIN dept d ON m.dept_id = d.dept_id LEFT JOIN rank r ON m.rank_id = r.rank_id WHERE 1=1
AND d.dept_id = #{dept}
AND r.rank_id = #{rank}
AND m.mem_name LIKE CONCAT('%', #{name}, '%')
ORDER BY m.mem_id DESC LIMIT #{limit} OFFSET #{start}

mapper.update:
UPDATE member
SET dept_id = #{deptId},
    rank_id = #{rankId}
WHERE mem_id = #{memId}
